function getActionArray(device, type, actionType)
{
    var actionArray = [];
    var keys = Object.keys(device);
    for (var ii = 0; ii < keys.length; ++ii)
    {
        if (device[keys[ii]].hasOwnProperty('type'))
        {
            if (device[keys[ii]].type == type)
            {
                if (device[keys[ii]].action == actionType)
                {
                    actionArray.push(device[keys[ii]]);
                }
            }
        }
    }
    return actionArray;
}
function scalarAlarmScan(device, actionType)
{
    var alarmFlag = false;
    var actionArray = getActionArray(device, 'scalar', actionType);
    if (actionArray.length > 0)
    {
        for (var ii = 0; ii < actionArray.length; ++ii)
        {
            actionArray[ii].alarm.value = 0;
            if (!isNaN(actionArray[ii].value))
            {
                if (actionArray[ii].value <= actionArray[ii].alarm.limits.low)  actionArray[ii].alarm.value = 1;
                if (actionArray[ii].value <= actionArray[ii].alarm.limits.lolo) actionArray[ii].alarm.value = 3;
                if (actionArray[ii].value >= actionArray[ii].alarm.limits.high) actionArray[ii].alarm.value = 2;
                if (actionArray[ii].value >= actionArray[ii].alarm.limits.hihi) actionArray[ii].alarm.value = 4;
            }
            if (actionArray[ii].alarm.value > 0)  alarmFlag = true
        }
    }
    return alarmFlag;
}

