        function addTracePointsToTimePlot(pts)
        {
            var now = new Date().getTime();
            var plotBeginning = now - timePlotLength;
            var removeDone = false;
            while(!removeDone)
            {
                if (timePlotTraces[0].x[0] < plotBeginning)
                {
                    for (var ii = 0; ii < timePlotTraces.length; ++ii)
                    {
                        timePlotTraces[ii].x.shift();
                        timePlotTraces[ii].y.shift();
                    }
                }
                else
                {
                    removeDone = true;
                }
            }
            for (var ii = 0; ii < timePlotTraces.length; ++ii)
            {
                timePlotTraces[ii].x.push(now);
                timePlotTraces[ii].y.push(pts[ii]);
            }    
            Plotly.newPlot("timePlotChart", timePlotTraces, timePlotLayout);
        }

